package basic_j;

public class OneD_arr {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] marks = {1, 2, 3, 4};

        for (int i = 0; i < marks.length; i++) 
        {
            System.out.println("Mark " + (i + 1) + ": " + marks[i]);
        }
	}

}
